mapper.select:
SELECT b.* FROM ( SELECT @RNUM:=@RNUM+1 AS ROWNUM , a.* FROM (SELECT @RNUM:=0) R , (SELECT * FROM hcc_notice_tb
where notice_status = 'Y' AND notice_type ='N'
order by notice_seq DESC ) a ) as b where ROWNUM between #{param1} and #{param2}

mapper.update:
update 
  hcc_notice_tb 
set notice_title = #{param2}
,   notice_content = #{param3}
,   notice_upd_date = NOW() 

where notice_seq = #{param1}

mapper.select:
select *
from hcc_like_tb
where like_seq = #{param1}
and like_code = #{param2}
and like_reg_seq = #{param3}

mapper.update:
update hcc_notice_tb
set notice_like_cnt = notice_like_cnt+1
where notice_seq = #{param1}
